<?php if (!defined('basePath')) exit('No direct script access allowed');$error = true;$query = "delete from ".$this->table_prefix."menu where menu_id='".$_POST['menuID']."'";$this->db->execute($query);function deleteChild($id){		global $system;		$query = "select menu_id from ".$system->table_prefix."menu where parent_id='".$id."'";		if($child = $system->db->getAll($query)){				foreach($child as $v){					$query = "delete from ".$system->table_prefix."menu where menu_id='".$_POST['menuID']."'";			$system->db->execute($query);			deleteChild($v['menu_id']);		}	}}if(deleteChild($_POST['menuID'])){	$error = false;}$response = array('error' => $error);echo json_encode($response);?>