<?php if (!defined('basePath')) exit('No direct script access allowed');/* Add/Edit URL */$fURL   	= $this->site->isMultiLang()?'page_url_'.$this->active_lang():'page_url';$addUrl		= $this->db->getOne("select ".$fURL." from ".$this->table_prefix."pages where page_switch='category_add' and parent_id='".$this->thisPageID()."'");$editUrl	= $this->db->getOne("select ".$fURL." from ".$this->table_prefix."pages where page_switch='category_edit' and parent_id='".$this->thisPageID()."'");$parentID 		= !$this->uri(4)?0:intval($this->uri(4));$parentID 		= @$this->uri(3)=='parent'?$parentID:0;$addUrl 		= $this->adminUrl().$addUrl.'/'.$parentID.$this->permalink().'?r='.base64_encode($this->thisUrl());$breadcrumbCat	= $this->breadcrumbCategory($parentID);?><div class="row">	<div class="col-md-4 col-lg-3">				<div class="box">			<div class="box-header with-border">				<div class="widget-header">				<h4 class="widget-title">Select Parent</h4>					<div class="widget-toolbar"><a data-toggle="collapse" data-parent="#accordion" href="#tree-container" aria-expanded="true" aria-controls="collapseOne" class="toggle-select"><i class="ace-icon fa fa-chevron-down"></i></a></div>				</div>			</div>				<div id="tree-container" class="box-body collapse in">				<div class="widget-main">					<div class="tree"><? require 'category.tree.php'; ?></div>				</div>			</div>		</div>	</div>	<div class="col-md-8 col-lg-9">		<div class="box">			<div class="box-header with-border">				<div class="widget-header">				<h4 class="widget-title"><?php echo $breadcrumbCat?></h4>					<div class="widget-toolbar">						<a href="<?=$addUrl?>" class="btn btn-sm btn-flat btn-info"><i class="fa fa-plus"></i> Add Category</a>					</div>				</div>			</div>				<div class="box-body">				<div class="widget-main">					<? require 'category.list.php'; ?>				</div>			</div>		</div>			</div></div>